      if (this.props.last.includes("Stop")) {
        return <NextToDiv>Short</NextToDiv>;
      } else {
        return <NextToDiv>Short</NextToDiv>;
      }
    } else {
      if (this.props.last.includes("Stop")) {
        return <NextToDiv>Long</NextToDiv>;
      } else {
        return <NextToDiv>Long</NextToDiv>;
      }
    }
  }
}

